import React, { Component } from 'react'
import { PushNotificationWorker } from '../serviceWorkerUtility'
export default class Home extends Component {
  async componentDidMount() {
    const swRegistration = await navigator.serviceWorker.register('service-worker-push.js')
    const permission = await PushNotificationWorker.requestNotificationPermission()
    console.log(swRegistration)
    PushNotificationWorker.showLocalNotification('This is title', 'this is the message', swRegistration);
  }
  render() {
    return (
      <div>
        <h1>Home page</h1>
      </div>
    )
  }
}
